// src/components/Panels.jsx
import React, { useState } from 'react';
import { panels, projects } from '../data/mockData';

const Panels = () => {
  const [selectedProjectId, setSelectedProjectId] = useState('');

  const filteredPanels = selectedProjectId
    ? panels.filter(panel => panel.projectId === parseInt(selectedProjectId))
    : panels;

  return (
    <div className="card-base">
      <h3>ðŸ“Š Paneles creados</h3>

      <select
        value={selectedProjectId}
        onChange={(e) => setSelectedProjectId(e.target.value)}
      >
        <option value="">ðŸ”Ž Ver todos los proyectos</option>
        {projects.map((project) => (
          <option key={project.id} value={project.id}>
            {project.name}
          </option>
        ))}
      </select>

      {filteredPanels.map(panel => (
        <div key={panel.id} className="card-panel">
          <p><strong>{panel.name}</strong></p>
          <p>{panel.description}</p>
          <p>Creado: {panel.createdAt}</p>
          <p>Ãšltima modificaciÃ³n: {panel.updatedAt ?? 'No registra'}</p>
          <p>Autor: {panel.author}</p>
        </div>
      ))}
    </div>
  );
};

export default Panels;
