import React, { useState } from 'react';
import { dataSources, projects } from '../data/mockData';

const DataSources = () => {
  const [selectedProjectId, setSelectedProjectId] = useState('');

  let filteredSources = dataSources;

  if (selectedProjectId) {
    const project = projects.find(p => p.id === parseInt(selectedProjectId));
    if (project) {
      filteredSources = dataSources.filter(ds =>
        project.dataSources.includes(ds.id)
      );
    }
  }

  return (
    <div className="card-base">
      <h3>ðŸ’§ Fuentes de datos</h3>

      <select
        value={selectedProjectId}
        onChange={(e) => setSelectedProjectId(e.target.value)}
      >
        <option value="">ðŸ”Ž Ver todos los proyectos</option>
        {projects.map(project => (
          <option key={project.id} value={project.id}>
            {project.name}
          </option>
        ))}
      </select>

      {filteredSources.map(source => (
        <div key={source.id}>
          <p><strong>{source.name}</strong></p>
          <p>Tipo: {source.type}</p>
          <p>Ãšltima actualizaciÃ³n: {source.lastUpdate}</p>
          <p>Total de registros: {source.totalRecords}</p>
        </div>
      ))}
    </div>
  );
};

export default DataSources;
