import React, { useState } from 'react';
import './Overview.css';
import Projects from '../components/Projects';
import DataSources from '../components/DataSources';
import Panels from '../components/Panels';
import Dashboards from '../components/Dashboards';
import { projects } from '../data/mockData';
import Layout from '../components/Layout';

const Overview = () => {
  const [selectedProjectId, setSelectedProjectId] = useState(projects[0].id);
  const [activeTab, setActiveTab] = useState("proyecto");

  const selectedProject = projects.find(p => p.id === parseInt(selectedProjectId));

  return (
    <Layout>
      <div className="tab-bar">
        <button
          className={activeTab === "proyecto" ? "active" : ""}
          onClick={() => setActiveTab("proyecto")}
        >
          üóÇÔ∏è Proyecto
        </button>
        <button
          className={activeTab === "fuentes" ? "active" : ""}
          onClick={() => setActiveTab("fuentes")}
        >
          üíß Fuentes
        </button>
        <button
          className={activeTab === "paneles" ? "active" : ""}
          onClick={() => setActiveTab("paneles")}
        >
          üìä Paneles
        </button>
        <button
          className={activeTab === "tableros" ? "active" : ""}
          onClick={() => setActiveTab("tableros")}
        >
          üìã Tableros
        </button>
      </div>

      {activeTab === "proyecto" && (
        <div className="overview-grid animated-fade-in">
          <Projects onSelectProject={setSelectedProjectId} />
          <div className="project-info card-base active">
            <h3>T√≠tulo del proyecto: {selectedProject.name}</h3>
            <p>Descripci√≥n: {selectedProject.description}</p>
            <p>Fuentes de datos: {selectedProject.dataSources.join(', ')}</p>
            <p>Fecha de creaci√≥n: {selectedProject.createdAt}</p>
            <p>√öltima modificaci√≥n: {selectedProject.updatedAt ?? 'No registra'}</p>
            <p>Autor: {selectedProject.author}</p>
            <p>Grupo: {selectedProject.group}</p>
          </div>
        </div>
      )}

      {activeTab === "fuentes" && (
        <div className="overview-grid animated-fade-in">
          <DataSources />
        </div>
      )}

      {activeTab === "paneles" && (
        <div className="overview-grid animated-fade-in">
          <Panels />
        </div>
      )}

      {activeTab === "tableros" && (
        <div className="overview-grid animated-fade-in">
          <Dashboards />
        </div>
      )}
    </Layout>
  );
};

export default Overview;
