import React, { useState } from 'react';
import { dashboards, projects } from '../data/mockData';

const Dashboards = () => {
  const [selectedProjectId, setSelectedProjectId] = useState('');

  const filteredDashboards = selectedProjectId
    ? dashboards.filter(d => d.projectId === parseInt(selectedProjectId))
    : dashboards;

  return (
    <div className="card-base">
      <h3>ðŸ“‹ Tableros creados</h3>

      <select
        value={selectedProjectId}
        onChange={(e) => setSelectedProjectId(e.target.value)}
      >
        <option value="">ðŸ”Ž Ver todos los proyectos</option>
        {projects.map((project) => (
          <option key={project.id} value={project.id}>
            {project.name}
          </option>
        ))}
      </select>

      {filteredDashboards.map(dashboard => (
        <div key={dashboard.id}>
          <p><strong>{dashboard.name}</strong></p>
          <p>{dashboard.description}</p>
          <p>Creado: {dashboard.createdAt}</p>
          <p>Ãšltima modificaciÃ³n: {dashboard.updatedAt ?? 'No registra'}</p>
          <p>Autor: {dashboard.author}</p>
        </div>
      ))}
    </div>
  );
};

export default Dashboards;
