// src/components/Layout.jsx
import React from 'react';
import './Layout.css';
import UserTopBar from './UserTopBar';

const Layout = ({ children, onSelectSection }) => {
  return (
    <div className="layout">
      <aside className="sidebar">
        <h2>Dashboard</h2>
        <nav>
          <button className="sidebar-link" onClick={() => onSelectSection('inicio')}>üè† Inicio</button>
          <button className="sidebar-link" onClick={() => onSelectSection('seguridad')}>üîí Seguridad</button>
          <button className="sidebar-link" onClick={() => onSelectSection('proyectos')}>üìÅ Proyectos</button>
          <button className="sidebar-link" onClick={() => onSelectSection('asistente')}>ü§ñ Asistente</button>
          <button className="sidebar-link" onClick={() => onSelectSection('fuentes')}>üíß Fuentes</button>
          <button className="sidebar-link" onClick={() => onSelectSection('paneles')}>üìä Paneles</button>
          <button className="sidebar-link" onClick={() => onSelectSection('tableros')}>üìã Tableros</button>
        </nav>
      </aside>

      {/* Contenedor de barra superior + contenido */}
      <div className="content-area">
        <UserTopBar username="admin_creangel" />
        <main className="main-content">{children}</main>
      </div>
    </div>
  );
};

export default Layout;
